import React, { useState } from 'react';
import API from '../../api';
import { useNavigate } from 'react-router-dom';

export default function ForgotPassword() {
  const [email, setEmail] = useState('');
  const [securityAnswer, setSecurityAnswer] = useState('');
  const [newPassword, setNewPassword] = useState('');
  const [step, setStep] = useState(1); // 1: ask email, 2: ask security answer and new password
  const [message, setMessage] = useState('');
  const [err, setErr] = useState('');
  const [loading, setLoading] = useState(false);
  const navigate = useNavigate();

  const submitEmail = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setErr('');
    try {
      // Check if email exists and get security question
      const res = await API.post('/auth/check-security-question', { email });
      if (res.data.securityQuestion) {
        setStep(2);
        setMessage('');
      } else {
        setErr('Email not found');
      }
    } catch (error) {
      setErr(error.response?.data?.message || 'Failed to verify email');
    } finally {
      setLoading(false);
    }
  };

  const submitReset = async (e) => {
    e.preventDefault();
    setLoading(true);
    setMessage('');
    setErr('');
    try {
      const res = await API.post('/auth/reset-password-security-question', { email, securityAnswer, newPassword });
      setMessage(res.data.message);
      setTimeout(() => {
        navigate('/login');
      }, 3000);
    } catch (error) {
      setErr(error.response?.data?.message || 'Failed to reset password');
    } finally {
      setLoading(false);
    }
  };

  return (
    <div className="row justify-content-center align-items-center min-vh-100">
      <div className="col-md-6 col-lg-4">
        <div className="card shadow-lg">
          <div className="card-header text-center bg-info text-white">
            <h4 className="mb-0"><i className="fas fa-unlock-alt me-2"></i>Forgot Password</h4>
          </div>
          <div className="card-body">
            {message && <div className="alert alert-success">{message}</div>}
            {err && <div className="alert alert-danger">{err}</div>}
            <form onSubmit={submit}>
              <div className="mb-3">
                <label className="form-label">Email Address</label>
                <input
                  type="email"
                  className="form-control"
                  value={email}
                  onChange={e => setEmail(e.target.value)}
                  required
                  placeholder="Enter your registered email"
                />
              </div>
              <div className="d-grid">
                <button className="btn btn-info" disabled={loading}>
                  {loading ? <><i className="fas fa-spinner fa-spin me-2"></i>Sending...</> : 'Send Reset Link'}
                </button>
              </div>
            </form>
            <div className="text-center mt-3">
              <button className="btn btn-link" onClick={() => navigate('/login')}>
                Back to Login
              </button>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
