import React, { useState, useEffect, useRef } from 'react';
import API from '../../api';
import Tesseract from 'tesseract.js';
import medicineNames from '../../data/medicineNames';
import manufacturerNames from '../../data/manufacturerNames';

export default function DonateForm() {
  const [name, setName] = useState('');
  const [manufacturer, setManufacturer] = useState('');
  const [manufacturerSuggestions, setManufacturerSuggestions] = useState([]);
  const [showManufacturerSuggestions, setShowManufacturerSuggestions] = useState(false);
  const manufacturerSuggestionsRef = useRef(null);
  const [expiry, setExpiry] = useState('');
  const [quantity, setQuantity] = useState(1);
  const [image, setImage] = useState(null);
  const [ocrLoading, setOcrLoading] = useState(false);
  const [msg, setMsg] = useState('');
  const [suggestions, setSuggestions] = useState([]);
  const [showSuggestions, setShowSuggestions] = useState(false);
  const suggestionsRef = useRef(null);

  useEffect(() => {
    if (name.length >= 3) {
      const filtered = medicineNames.filter(med =>
        med.toLowerCase().includes(name.toLowerCase())
      ).slice(0, 10); // limit to 10 suggestions
      setSuggestions(filtered);
      setShowSuggestions(filtered.length > 0);
    } else {
      setShowSuggestions(false);
      setSuggestions([]);
    }
  }, [name]);

  // Close suggestions dropdown if clicked outside
  useEffect(() => {
    const handleClickOutside = (event) => {
      if (suggestionsRef.current && !suggestionsRef.current.contains(event.target)) {
        setShowSuggestions(false);
      }
    };
    document.addEventListener('mousedown', handleClickOutside);
    return () => {
      document.removeEventListener('mousedown', handleClickOutside);
    };
  }, []);

  const handleSuggestionClick = (suggestion) => {
    setName(suggestion);
    setShowSuggestions(false);
  };

  // === OCR with Server ===
  const handleOCRServer = async () => {
    if (!image) return setMsg('Upload image first');
    setOcrLoading(true);
    setMsg('Sending image to server OCR service...');

    try {
      const fd = new FormData();
      fd.append('image', image);
      const res = await API.post('/medicines/ocr', fd, {
        headers: { 'Content-Type': 'multipart/form-data' },
      });

      if (res.data && res.data.text) {
        const text = res.data.text;

        // Regex patterns for multiple expiry date formats
        const regexes = [
          /\b(?:EXP(?:IRY)?(?:\s*DATE)?\.?\s*[:\-]?\s*)?(\d{1,2}[\/\-\.]\d{1,2}[\/\-\.]\d{2,4})/i, // 12/03/2026
          /\b(?:EXP(?:IRY)?(?:\s*DATE)?\.?\s*[:\-]?\s*)?(\d{1,2}[\/\-\.]\d{2,4})/i,                // 12/2026
          /\b(?:EXP(?:IRY)?(?:\s*DATE)?\.?\s*[:\-]?\s*)?([A-Za-z]{3,}\.?[\s\-]?\d{2,4})/i,         // AUG 2026, MAR.2028
          /\b(20\d{2})\b/,                                                                       // Year only
        ];

        let match = null;
        for (let re of regexes) {
          match = text.match(re);
          if (match) break;
        }

        if (match && match[1]) {
          let dateStr = match[1].trim().replace(/\./g, '').toUpperCase();
          let parsedDate = null;

          // Month name mapping
          const months = {
            JAN: 1, FEB: 2, MAR: 3, APR: 4, MAY: 5, JUN: 6,
            JUL: 7, AUG: 8, SEP: 9, OCT: 10, NOV: 11, DEC: 12,
          };

          if (/^\d{1,2}[\/\-]\d{4}$/.test(dateStr)) {
            // MM/YYYY
            const [mm, yyyy] = dateStr.split(/\/|-/);
            parsedDate = new Date(Number(yyyy), Number(mm), 0);
          } else if (/^\d{1,2}[\/\-]\d{1,2}[\/\-]\d{2,4}$/.test(dateStr)) {
            // DD/MM/YYYY
            const parts = dateStr.split(/\/|-/);
            let dd = parts[0], mm = parts[1], yyyy = parts[2];
            if (yyyy.length === 2) yyyy = '20' + yyyy;
            parsedDate = new Date(Number(yyyy), Number(mm) - 1, Number(dd));
          } else {
            // Month names like AUG 2026
            const parts = dateStr.split(/[\s\-]/);
            const monthTxt = parts[0].slice(0, 3); // e.g., AUG
            const yyyy = parts[1];
            if (months[monthTxt]) {
              parsedDate = new Date(Number(yyyy), months[monthTxt], 0);
            } else if (/^20\d{2}$/.test(dateStr)) {
              parsedDate = new Date(Number(dateStr), 11, 31); // Dec 31 of that year
            }
          }

          if (parsedDate && !isNaN(parsedDate)) {
            const yyyy = parsedDate.getFullYear();
            const mm = String(parsedDate.getMonth() + 1).padStart(2, '0');
            const dd = String(parsedDate.getDate()).padStart(2, '0');
            const finalDate = `${yyyy}-${mm}-${dd}`;
            setExpiry(finalDate);
            setMsg(
              'Server OCR found possible expiry: ' +
                finalDate +
                '. Full text: ' +
                text.slice(0, 200),
            );
          } else {
            setMsg(
              'OCR found text but could not parse a valid date. Text: ' +
                text.slice(0, 200),
            );
          }
        } else {
          setMsg(
            'No expiry pattern found in OCR text. Extracted: ' +
              text.slice(0, 200),
          );
        }
      } else {
        setMsg('Server OCR returned no text.');
      }
    } catch (err) {
      setMsg('Server OCR failed: ' + (err.response?.data?.message || err.message));
    } finally {
      setOcrLoading(false);
    }
  };

  // === Form submit ===
  const submit = async (e) => {
    e.preventDefault();
    try {
      const payload = { name, manufacturer, expiryDate: expiry, quantity };
      await API.post('/medicines/donate', payload);
      setMsg('Donation submitted');
      setName('');
      setManufacturer('');
      setExpiry('');
      setQuantity(1);
      setImage(null);
    } catch (err) {
      setMsg(err.response?.data?.message || err.message);
    }
  };

  return (
    <div className="container-fluid">
      <div className="row justify-content-center">
        <div className="col-lg-8">
          <div className="card shadow">
            <div className="card-header bg-primary text-white">
              <h4 className="mb-0">
                <i className="fas fa-pills me-2"></i>Donate Medicine
              </h4>
            </div>
            <div className="card-body">
              {msg && (
                <div className="alert alert-info">
                  <i className="fas fa-info-circle me-2"></i>
                  {msg}
                </div>
              )}
              <form onSubmit={submit} autoComplete="off">
                <div className="row mb-3" style={{ position: 'relative' }}>
                  <div className="col-md-6" ref={suggestionsRef}>
                    <label htmlFor="name" className="form-label">
                      <i className="fas fa-tag me-1"></i>Medicine Name
                    </label>
                    <input
                      id="name"
                      className="form-control"
                      placeholder="e.g., Aspirin"
                      value={name}
                      onChange={(e) => setName(e.target.value)}
                      required
                      autoComplete="off"
                      onFocus={() => {
                        if (suggestions.length > 0) setShowSuggestions(true);
                      }}
                    />
                    {showSuggestions && (
                      <ul className="list-group position-absolute w-100" style={{ zIndex: 1000, maxHeight: '200px', overflowY: 'auto' }}>
                        {suggestions.map((suggestion, index) => (
                          <li
                            key={index}
                            className="list-group-item list-group-item-action"
                            style={{ cursor: 'pointer' }}
                            onClick={() => handleSuggestionClick(suggestion)}
                          >
                            {suggestion}
                          </li>
                        ))}
                      </ul>
                    )}
                  </div>
                  <div className="col-md-6">
                    <label htmlFor="manufacturer" className="form-label">
                      <i className="fas fa-industry me-1"></i>Manufacturer
                    </label>
                    <input
                      id="manufacturer"
                      className="form-control"
                      placeholder="e.g., Bayer"
                      value={manufacturer}
                      onChange={(e) => setManufacturer(e.target.value)}
                    />
                  </div>
                </div>
                <div className="row mb-3">
                  <div className="col-md-6">
                    <label htmlFor="expiry" className="form-label">
                      <i className="fas fa-calendar-alt me-1"></i>Expiry Date (or use OCR)
                    </label>
                    <input
                      id="expiry"
                      type="date"
                      className="form-control"
                      value={expiry}
                      onChange={(e) => setExpiry(e.target.value)}
                    />
                  </div>
                  <div className="col-md-6">
                    <label htmlFor="quantity" className="form-label">
                      <i className="fas fa-hashtag me-1"></i>Quantity
                    </label>
                    <input
                      id="quantity"
                      type="number"
                      min="1"
                      className="form-control"
                      value={quantity}
                      onChange={(e) => setQuantity(e.target.value)}
                    />
                  </div>
                </div>
                <div className="mb-3">
                  <label htmlFor="image" className="form-label">
                    <i className="fas fa-upload me-1"></i>Upload Image (label/box with expiry)
                  </label>
                  <input
                    id="image"
                    type="file"
                    accept="image/*"
                    className="form-control"
                    onChange={(e) => setImage(e.target.files[0])}
                  />
                </div>
                <div className="mb-3 d-flex flex-wrap gap-2">
                  <button
                    type="button"
                    className="btn btn-outline-success"
                    onClick={handleOCRServer}
                    disabled={ocrLoading}
                  >
                    {ocrLoading ? (
                      <>
                        <i className="fas fa-spinner fa-spin me-1"></i>OCR...
                      </>
                    ) : (
                      <>
                        <i className="fas fa-server me-1"></i>Extract Expiry (Server)
                      </>
                    )}
                  </button>
                  <button className="btn btn-success" type="submit">
                    <i className="fas fa-paper-plane me-1"></i>Submit Donation
                  </button>
                </div>
              </form>
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}
